using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Mvc;
using System.Web.Routing;
using Autofac;
using PaySharp.Alipay;
using PaySharp.Core;
using PaySharp.Core.Mvc;

namespace AlipayDemo
{
    public class MvcApplication : System.Web.HttpApplication
    {
        protected void Application_Start()
        {
            AreaRegistration.RegisterAllAreas();
            RouteConfig.RegisterRoutes(RouteTable.Routes);

            var containerBuilder = new ContainerBuilder();

            
            PaySharpConfig.Register(typeof(MvcApplication), containerBuilder, a =>
            {
                var gateways = new Gateways();
                //gateways.RegisterAlipay();

                var alipayMerchant = new PaySharp.Alipay.Merchant
                {
                    AppId = "2016081600256163",
                    NotifyUrl = "http://localhost:57618/Notify",
                    ReturnUrl = "http://localhost:57618/Notify",
                    AlipayPublicKey = "",
                    Privatekey = ""
                };

                gateways.Add(new AlipayGateway(alipayMerchant)
                {
                    GatewayUrl = "https://openapi.alipaydev.com"
                });

                return gateways;
            });
        }
    }
}
